import React, { useContext, useState } from 'react'
import { useParams } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import MyContext from '../../context/MyContext'

const Output = () => {
    const navigate = useNavigate()
    const { id } = useParams()
    const { notes, setNotes } = useContext(MyContext)

    const [save, setSave] = useState(false)


     let filtered = notes.filter(element => element.id.match(id))

     const index = notes.indexOf(filtered[0])

     const delNote = () => {   
        setNotes(prevNotes => {
             prevNotes.splice(index,1)
             return prevNotes  
        })
        navigate("/")
    }

    const editor = () => {
        setSave(prevState => !prevState)
    }


return (
<div>
<button> Edit</button>

    {save ? <div>
                <h1>{filtered[0].title}</h1>
                <span>{filtered[0].time}</span>
                <p className='text'>{filtered[0].body}</p> 
                <button onClick={delNote}>Delete</button>

            </div> 
    : 
            <div>
                <textarea name="" value={filtered[0].title} cols="60" rows="1"/> <br/>
                <textarea name="" value={filtered[0].body} cols="60" rows="40"/>
            </div>}

</div> 
  )
}

export default Output
