import React, { useContext, useState } from 'react'
import { useParams } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import MyContext from '../../context/MyContext'

const Output = () => {
    const navigate = useNavigate()
    const { id } = useParams()
    const { notes, noteInput, setNoteInput, setNotes, handleChange } = useContext(MyContext)

    const [save, setSave] = useState(true)




     let filtered = notes.find(element => element.id.match(id))

     const index = notes.indexOf(filtered)

     const delNote = () => {   
        setNotes(prevNotes => {
             prevNotes.splice(index,1)
             return prevNotes  
        })
        navigate("/")
    }

    const editor = () => {
        if (save) {
            setNoteInput(filtered)

        } else {

        console.log(noteInput)
        console.log(notes)

        const newArray = [...notes]
        newArray[index] = noteInput

        setNotes(newArray)
        }


    }


return (
<div>
<button onClick={editor}> {save ? "Edit" : "Save"}</button>
<button onClick={delNote}>Delete</button>

    {save ? <div>
                <h1>{filtered.title}</h1>
                <span>{filtered.time}</span>
                <p className='text'>{filtered.body}</p> 


            </div> 
    : 
            <div>
                <textarea name="title" defaultValue={noteInput.title} onChange={handleChange} cols="60" rows="1"/> <br/>
                <textarea name="body" defaultValue={noteInput.body} onChange={handleChange} cols="60" rows="40"/>
            </div>}

</div> 
  )
}

export default Output
