import {useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import MyContext from '../../context/MyContext'

const AddNotes = () => {
  const {notes, handleSubmit, handleChange} = useContext(MyContext)

  const navigate = useNavigate()


  useEffect(() => {
    return () => {
      localStorage.setItem("noteData", JSON.stringify(notes));
    }
})

 const submit = () => {
   async 
   handleSubmit()


 }


  return (
        <div className="note">
          <form>
            <label htmlFor="title">Note Title</label>
            <input type="text" name="title" id="title" onChange={handleChange} value={notes.title} /> <br />

            <label htmlFor="body">...enter notes</label>
            <textarea name="body" id="body" onChange={handleChange} value={notes.body} />

            <button onClick={submit}>Submit</button>
        </form>
        </div>
  )
}

export default AddNotes
