import React, { useContext, useState } from 'react'
import { useParams } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import MyContext from '../../context/MyContext'

const Output = () => {
    const navigate = useNavigate()
    const { id } = useParams()
    const { notes, setNotes } = useContext(MyContext)

    const [save, setSave] = useState(true)


     let filtered = notes.filter(element => element.id.match(id))
     const index = notes.indexOf(filtered[0])
    const delNote = () => {

        setNotes(prevNotes => {
             prevNotes.splice(index,1)
             return prevNotes  
        })
        navigate("/")
    }

return (
<div>

    {save ? <div>
        <h1>{filtered[0].title}</h1>
        <span>{filtered[0].time}</span>
        <p className='text'>{filtered[0].body}</p> 
        <button onClick={delNote}>Delete</button>

    </div> 
    : 
    <div>
     <textarea name="" id="" cols="30" rows="10"></textarea>
    </div>}

</div> 
  )
}

export default Output
