import React, { useContext } from 'react'
import { useParams } from 'react-router-dom'
import MyContext from '../../context/MyContext'

const Output = () => {

    const { id } = useParams()
    const { notes, setNotes } = useContext(MyContext)


     let filtered = notes.filter(element => element.id.match(id))

    const delNote = () => {
        const index = notes.indexOf(filtered[0])

        setNotes(prevNotes => {
             let removed = prevNotes.splice(index,1)
             console.log(removed )
             return prevNotes

        })

    }

    return (

    <div>
        <h1>{filtered[0].title}</h1>
        <span>{filtered[0].time}</span>
        <p className='text'>{filtered[0].body}</p> 
        <button onClick={delNote}>Delete</button>
    </div>
  )
}

export default Output
