import {useContext} from 'react'
import { Link } from 'react-router-dom'
import Split from 'react-split'
import MyContext from '../../context/MyContext'

const Notes = () => {

    const { notes } = useContext(MyContext)



  return (
    <div className='parent'>
         <Split
          sizes={[30,70]}
          direction="vertical"
          className='sidebar'
        >
          <div>
          {notes.map((note, i) =>
           { return <p key={i}><Link to={note.id}>{note.colorName}</Link></p> }
        )}
          </div>


        </Split>

        <Split
          sizes={[70,70]}
          direction="vertical"
          className='main-'
        >
         <h1>Good Morning people</h1>
         <p>We are here for you</p>


        </Split>
    </div>
  )
}

export default Notes
